def xor_hex_strings(hex1, hex2):
    # Chuyển đổi chuỗi hex sang dạng bytes
    bytes1 = bytes.fromhex(hex1)
    bytes2 = bytes.fromhex(hex2)
    
    # XOR từng byte của hai chuỗi
    xor_result = bytes(b1 ^ b2 for b1, b2 in zip(bytes1, bytes2))
    
    # Chuyển kết quả XOR sang dạng hex
    hex_output = xor_result.hex()
    
    # Chuyển kết quả XOR sang dạng ASCII, bỏ các ký tự không hiển thị
    ascii_output = ''.join(chr(b) if 32 <= b <= 126 else '.' for b in xor_result)
    
    return hex_output, ascii_output

# Dữ liệu đầu vào
hex_str1 = ""
hex_str2 = ""

# Kết quả XOR
hex_result, ascii_result = xor_hex_strings(hex_str1, hex_str2)
print("Hex:", hex_result)
print("ASCII:", ascii_result)
